<js>
result.toArray().map(o=>{
    var detail = String(o.select('dt a').attr('href')).replace('//','https://');
    var J = org.jsoup.Jsoup.parse(java.ajax(detail));
    return {
       name:  o.select('dt a').text(),
       author:String(o.select('.ls a').text()).replace('作者：',''),
       tags:  String(o.select('.bq span:eq(1)').text()).replace('类别：',''),
       num: String(o.select('.bq span:eq(2)').text()).replace('字数：',''),
       latest: String(J.select('dl.NewsChapter a').last().text()).replace(/[\(（].*[求更谢乐发订合补加].*[）\)]/,''),
       intro: String(o.select('li:eq(2)').text()).replace('简介：','').trim(),
       cover: o.select('img').attr('src'),
       catalogUrl: 'https://www.17k.com'+J.select('.read a').attr('href')
    }
});
</js>

